import React, {useState, useEffect} from 'react';
import MainVideo from './MainVideo.js';
import '../styles/mainpage.css';
import axios from 'axios';

function MainPage(title,desc,url) {
    //API call to video
    const apiURL = "http://localhost:8080/?startIndex="+{start}+"&count="+{count}

    var currVid = {};

    var [allVid, setAllVid] = useState([]);
    var [start, setStart] = useState(0);
    var [count, setCount] = useState(4);

    useEffect(() => {
        fetchData();
    },[])

    const fetchData=()=> {
        axios.get(apiURL)
        .then(res=>res.data.data)
        .then(data=>setAllVid(data))
        .then({
            currVid:{
                title: allVid[0]?.metadata.title
            }
        })
    }

    return ( 
        <div className='mainpage'>
            <div className='leftSide'>
                <MainVideo
                    urls={allVid[0]?.url}
                    title= {currVid.title} 
                    desc={allVid[0]?.metadata.description} 
                    
                />

            </div>
            <div className='rightSide'>
                <div>
                </div>
            </div>
        </div>
    );
}

export default MainPage;
