import MainVideo from './MainVideo.js';
import '../styles/mainpage.css';
import axios from 'axios';

function MainPage(title,desc,url) {
    //API call to video
    const apiURL = "http://localhost:8080/?startIndex="+{start}+"&count="+{count}


    var currVid = useRef({
        urls: '',
        title: '',
        desc: '',
    });

    var [allVid, setAllVid] = useState([]);
    var [start, setStart] = useState(0);
    var [count, setCount] = useState(4);

    useEffect(() => {
        fetchData();
    },[])

    const fetchData=()=> {
        axios.get(apiURL)
        .then(res=>res.data.data)
        .then(data=> {
            setAllVid(data)
            currVid.current = {
                urls: data[0].assets,
                title: data[0].metadata.title,
                desc: data[0].metadata.description,
            }
        })
        .then(console.log())
    }

    return ( 
        <div className='mainpage'>
            <div className='leftSide'>
                <MainVideo
                    urls={allVid[4]?.assets}
                    title= {allVid[0]?.metadata.title} 
                    desc={allVid[0]?.metadata.description} 
                    
                />

            </div>
            <div className='rightSide'>
                <div>
                </div>
            </div>
        </div>
    );
}

export default MainPage;
