import React, {useState, useEffect, useRef} from 'react';
import MainVideo from './MainVideo.js';
import '../styles/mainpage.css';
import axios from 'axios';

function MainPage() {
    const [startIndex, setStartIndex] = useState(0);
    const [count, setCount] = useState(5);
    const [allVid, setAllVid] = useState([]);

    const currVid = useRef({
        urls:'',
        title:'',
        desc:''
    })

    //Api call
    const getVidAPI = async (url) => {
        try{
            const {data} = await axios.get(url);
            return data;
        } catch(e){
            console.log(e);
        }
    }

    //Function for getting video data
    const fetchVidData = () => {
        getVidAPI("http://localhost:8080/?startIndex="+ startIndex +"&count="+ count)
            .then(({data})=>{
                setCurrVid(data[0]);
                setAllVid(data);
            })
    }

    useEffect(()=>{
        fetchVidData()
    }, [startIndex]) 


    return ( 
        <div className='mainpage'>
            <div className='leftSide'>
                <MainVideo
                    urls={currVid?.assets}
                    title= {currVid?.metadata.title} 
                    desc={currVid?.metadata.description} 
                    
                />

            </div>
            <div className='rightSide'>
                <div>
                </div>
            </div>
        </div>
    );
}

export default MainPage;
