import React from 'react';
import '../styles/que.css';

function QuedVideos({vidArr, setStartIndex}) {

    const vidObj = [];

    //We will change the current by the index + current
    const changeCurr = (increment)=>{
        setStartIndex(startIndex + increment);
    }

    for(let i = 0; i < vidArr.length; i++){
        vidObj.push({
            thumbnail: vidArr[i].thumbnails[2].url,
            title: vidArr[i].metadata.title,
            time: vidArr[i].metadata.duration
        })
    }

    console.log(vidObj[4])
    return (
        <div className="queue">
            {
                vidObj.map((vid,index)=>{
                    return(
                        <div onClick = {changeCurr(index)}>
                            <img src = {vid.thumbnail}/>
                        </div>
                        
                    )
                })
            }
        </div>
    );
}

export default QuedVideos;
