import React, {useState, useEffect} from 'react';
import MainVideo from './MainVideo.js';
import '../styles/mainpage.css';

function MainPage() {
    const [currVid, setCurrVid] = useState([]);
    const [allVid, setAllVid] = useState([]);
    const [start, setStart] = useState(0);
    const [count, setCount] = useState(4)

    //There was an issue calling API directly with URL, to bypass such issue we would need to use a proxy
    useEffect(() => {
        fetch("http://localhost:8080/?startIndex="+{start}+"&count="+{count})
        .then((res)=> res.json())
        .then((data)=> data.data)
        .then((vData)=> setAllVid(vData))
        setCurrVid(allVid[0])

    }, [])

    //Now that we have have the data stored in allVid and currVid, we need to be able to take out the proper one
    //The paths for the data are as follows
    //thumbnails: in array: each array split up with a {url, size, width, height}
    //metadata->{title, description, publishDate, slug, duration}
    //MOST IMPORTANT ONE VIDEO QUALITIES = assets: in array(from low to high)->{url,height,width}

    
    return ( 
        <div className='mainpage'>
            <div className='leftSide'>
                <MainVideo url= "" title="H" desc="ya"/>
            </div>
            <div className='rightSide'>
                <div>{JSON.stringify(allVid[0])}</div>
            </div>
        </div>
    );
}

export default MainPage;
