import React, {useState, useEffect} from 'react';
import MainVideo from './MainVideo.js';
import '../styles/mainpage.css';

function MainPage() {
    const [currVid, setCurrVid] = useState([]);
    const [allVid, setAllVid] = useState([]);

    //There was an issue requesting the API from the website so I made a local copy
    useEffect(() => {
        fetch("http://localhost:8080/","3")
        .then((res)=> res.json())
        .then((data)=> data.data)
        .then((vData)=> setAllVid(vData))
        setCurrVid(allVid[0])

    }, [])


    return ( 
        <div className='mainpage'>
            <div className='leftSide'>
                <MainVideo title="H" desc="ya"/>
            </div>
            <div className='rightSide'>
                <div>{JSON.stringify(allVid[0])}</div>
            </div>
        </div>
    );
}

export default MainPage;
