import MainVideo from './MainVideo.js';
import '../styles/mainpage.css';
import axios from 'axios';

function MainPage(title,desc,url) {

    const [videoArr, setVideoArr] = useState([]);
    const [theaterMode, setTheaterMode] = useState(false);
    const [startIndex, setStartIndex] = useState(0);
    const video = useRef({
        URLs: '',
        title: '',
        description: '',
    });


    const getVideoData = (startIndex, videoCount) => {
        getIGNData(`http://localhost:${portNumber}/videos?startIndex=${startIndex}&count=${videoCount}`)
            .then(({ data }) => {
                console.log(data);
                video.current = {
                    URLs: data[0].assets,
                    title: data[0].metadata.title,
                    description: data[0].metadata.description,
                };
                setVideoArr(data);
            })
            .catch(e => {
                console.log(e);
            })
    }

    useEffect(() => {
        getVideoData(startIndex, 10)
    }, [startIndex]);

    return ( 
        <div className='mainpage'>
            <div className='leftSide'>
                <MainVideo
                    urls={'allVid[4]?.assets'}
                    title= {'allVid[0]?.metadata.title'} 
                    desc={'allVid[0]?.metadata.description'} 
                    
                />

            </div>
            <div className='rightSide'>
                <div>
                </div>
            </div>
        </div>
    );
}

export default MainPage;
